import { body } from "express-validator";
const emailValidation = body("email").exists().withMessage("EMAIL \u043E\u0431\u044F\u0437\u0430\u0442\u0435\u043B\u0435\u043D.").trim().isString().withMessage("E-mail \u0434\u043E\u043B\u0436\u0435\u043D \u0431\u044B\u0442\u044C \u0441\u0442\u0440\u043E\u043A\u043E\u0439.").isEmail().withMessage("\u0424\u043E\u0440\u043C\u0430\u0442 EMAIL \u043D\u0435\u0434\u0435\u0439\u0441\u0442\u0432\u0438\u0442\u0435\u043B\u0435\u043D. \u041F\u0440\u0438\u043C\u0435\u0440: google@google.com.");
const loginValidation = body("login").exists().withMessage("\u041B\u043E\u0433\u0438\u043D \u043E\u0431\u044F\u0437\u0430\u0442\u0435\u043B\u0435\u043D.").trim().isString().withMessage("\u041B\u043E\u0433\u0438\u043D \u0434\u043E\u043B\u0436\u0435\u043D \u0431\u044B\u0442\u044C \u0441\u0442\u0440\u043E\u043A\u043E\u0439.").isLength({ min: 3, max: 10 }).withMessage("\u041B\u043E\u0433\u0438\u043D \u0434\u043E\u043B\u0436\u0435\u043D \u0441\u043E\u0434\u0435\u0440\u0436\u0430\u0442\u044C \u043E\u0442 3 \u0434\u043E 10 \u0441\u0438\u043C\u0432\u043E\u043B\u043E\u0432.").matches("^[a-zA-Z0-9_-]*$").withMessage("\u041B\u043E\u0433\u0438\u043D \u043C\u043E\u0436\u0435\u0442 \u0441\u043E\u0434\u0435\u0440\u0436\u0430\u0442\u044C \u0442\u043E\u043B\u044C\u043A\u043E \u043B\u0430\u0442\u0438\u043D\u0441\u043A\u0438\u0435 \u0431\u0443\u043A\u0432\u044B (a-z, A-Z), \u0446\u0438\u0444\u0440\u044B (0-9), \u0434\u0435\u0444\u0438\u0441 \u0438 \u043D\u0438\u0436\u043D\u0435\u0435 \u043F\u043E\u0434\u0447\u0435\u0440\u043A\u0438\u0432\u0430\u043D\u0438\u0435.");
const passwordValidation = body("password").exists().withMessage("\u041F\u0430\u0440\u043E\u043B\u044C \u043E\u0431\u044F\u0437\u0430\u0442\u0435\u043B\u0435\u043D.").isLength({ min: 6, max: 20 }).withMessage("\u041F\u0430\u0440\u043E\u043B\u044C \u0434\u043E\u043B\u0436\u0435\u043D \u0441\u043E\u0434\u0435\u0440\u0436\u0430\u0442\u044C \u043E\u0442 6 \u0434\u043E 20 \u0441\u0438\u043C\u0432\u043E\u043B\u043E\u0432.");
const userInputValidation = [
  emailValidation,
  loginValidation,
  passwordValidation
];
export {
  userInputValidation
};
